def banner():
    os.system("cls" if os.name == "nt" else "clear")
    banner_text = decode_str(ENCODED_BANNER)
    title_text = decode_str(ENCODED_TITLE)
    try:
        print(render(banner_text, colors=["cyan", "white"]))
    except:
        print(banner_text)
    print(COLORS['cyan'] + title_text + COLORS['reset'])

logging.basicConfig(level=logging.WARNING, format='%(asctime)s - %(message)s')

success_count = 0
unsuccess_count = 0
counter_lock = asyncio.Lock()

def print_stats():
    sys.stdout.write(f"\r{COLORS['green']}successful: {success_count}{COLORS['reset']} {COLORS['red']}unsuccessful: {unsuccess_count}{COLORS['reset']}")
    sys.stdout.flush()

banner()

# Password Protection
password = input("Enter script password: ").strip()
if password != decode_str(ENCODED_PASSWORD):
    print("Incorrect password!")
    sys.exit(1)

session_id = input("Session ID: ").strip()
dm_url = input("Group chat URL: ").strip()
target_name = input("Target Name (will replace {target} in messages): ").strip() or "USER"

try:
    task_count = int(input("Number of concurrent tasks (default 30, max 80): ").strip() or "30")
    if task_count > 80:
        task_count = 80
except:
    task_count = 30

async def send_loop(context):
    global success_count, unsuccess_count
    page = await context.new_page()
    # Disable features to save RAM
    await page.route("**/*.{png,jpg,jpeg,gif,webp,svg,mp4,webm,ogg}", lambda route: route.abort())
    await page.route("**/ads/**", lambda route: route.abort())
    
    page.set_default_timeout(600000)
    page.set_default_navigation_timeout(600000)
    
    msg_count = 0
    while True:
        try:
            await page.goto(dm_url, wait_until='domcontentloaded', timeout=600000)
            msg_input = page.locator('div[aria-label="Message"][role="textbox"]')
            await msg_input.wait_for(timeout=600000)
            
            while True:
                try:
                    base_msg = random.choice(MESSAGES)
                    msg = base_msg.replace("{target}", target_name)
                    await msg_input.fill(msg, timeout=600000)
                    await page.keyboard.press("Enter")
                    
                    async with counter_lock:
                        success_count += 1
                        msg_count += 1
                        print_stats()
                    
                    if msg_count >= 500:
                        msg_count = 0
                        break
                        
                    await asyncio.sleep(random.uniform(2, 5))
                except Exception:
                    async with counter_lock:
                        unsuccess_count += 1
                        print_stats()
                    await asyncio.sleep(5)
                    try:
                        await msg_input.wait_for(timeout=600000)
                    except:
                        break
        except Exception:
            await asyncio.sleep(10)

async def main():
    async with async_playwright() as p:
        # Browser optimization for low-end devices
        browser = await p.chromium.launch(
            headless=True, 
            args=[
                '--no-sandbox', 
                '--disable-gpu', 
                '--disable-dev-shm-usage',
                '--disable-extensions',
                '--disable-setuid-sandbox',
                '--no-first-run',
                '--no-zygote',
                '--js-flags="--max-old-space-size=512"'
            ]
        )
        context = await browser.new_context()
        await context.add_cookies([{
            "name": "sessionid",
            "value": session_id,
            "domain": ".instagram.com",
            "path": "/",
            "httpOnly": True,
            "secure": True,
            "sameSite": "None"
        }])

        logging.info(f"Starting {task_count} concurrent tasks...")
        tasks = [asyncio.create_task(send_loop(context)) for _ in range(task_count)]

        try:
            await asyncio.gather(*tasks)
        except KeyboardInterrupt:
            pass
        finally:
            print_stats()
            print()
            await browser.close()

if __name__ == "__main__":
    asyncio.run(main())
